mapper.select:
select id, title, title_img as titleImg, type, weburl, abstracte, auther, point_number,classify as pointNumber ,users as user,
from information_all where checked=1 AND id in
#{item}

SELECT i.id, i.create_date, i.title, i.title_img, i.classify, i.type, i.weburl, i.abstracte, i.auther, i.point_number, o.id, i.checked FROM information_all AS i INNER JOIN three_type AS r ON i.type = r.id INNER JOIN two_type AS t ON r.two_type = t.id INNER JOIN one_type AS o ON t.one_type = o.id
i.checked=#{checked}
AND o.id = #{type}
AND i.title LIKE concat('%',#{title},'%')
order by i.id desc

select information_all.id, information_all.create_date, information_all.title, information_all.title_img, information_all.type, information_all.weburl, information_all.abstracte, information_all.auther, information_all.point_number, information_all.checked, information_all.users,information_all.classify,information_all.competitive,information_all.user_brand,information_all.stick,information_all.solve users.pet_name AS petName, users.head_img AS headImg from information_all INNER JOIN users on users.id=information_all.users
type = #{type,jdbcType=INTEGER}
AND title like concat(concat('%',#{title}),'%')
AND checked=#{checked}
order by id desc
